import styled from "styled-components";
import { Link } from 'react-router-dom';
import '../App.css';
import { TextField, Button } from '@mui/material';
import { useState } from 'react';

const MainWindow = styled.div`
  width: 450px;
  margin: 0 auto;
  margin-top: 180px;
  background-color: #ffffff;
`

const SwitchSignIn = styled.div`
  width: 100%;
  height: 60px;
  display: flex;
  align-items: center;
  a{
    text-decoration: none;
    color: #787d85;
    text-transform: uppercase;
    letter-spacing: 1px;
    font-size: 11px;
    font-weight: 600;
    margin-left: 40px;
    :hover{
      color: black;
      transition: 1500ms;
    }
  }
`

const MainContainer = styled.div`
  width: 84%;
  margin: 0 auto;
  #SimpleLine{
    margin: unset;
    width: 100%;
    border: none;
    border-top: 1px solid grey;
    opacity: 1;
  }
  #SignInLine{
    margin: unset;
    width: 14%;
    border: none;
    border-top: 2px solid #f56c2d;
    opacity: 1;
  }
  #SignUpLine{
    margin: unset;
    margin-left: 85px;
    width: 50%;
    border: none;
    border-top: 2px solid #f56c2d;
    opacity: 1;
  }
  #headline{
    margin: 30px 0 20px 0px;
    font-size: 24px;
    font-weight: normal;
    color: black;
  }
  .MuiInputBase-root{
    margin-bottom: 20px;
  }
  #outlined-basic{
    width: 350px;
  }
  .MuiButtonBase-root{
    background-color: #f56c2d;
    width: 100%;
    font-size: 14px;
    text-transform: none;
    margin: 30px 0 50px 0px;
    :hover{
      transition: 800ms;
      background-color: #c85e36;
    }
  }
`

const SignUp = () => {

  const [overflow, setOverflow] = useState(0);
  const [login, setLogin] = useState(0);
  const [password, setPassword] = useState(0);
  const [getLogAndPass, setGetLogAndPass] = useState(false);

  const getData = (val) => {
    setLogin(val.target.value)
    setGetLogAndPass(false)
    console.warn(val.target.value)
  }


  return (
    <>
      <Link to='/Dashboard'>Домой</Link>
      <MainWindow>
        <SwitchSignIn>
          <a href="#" onClick={() => setOverflow(1)}>Войти</a>
          <a href="#" onClick={() => setOverflow(0)}>Создать учётную запись</a>
        </SwitchSignIn>
        {
          overflow === 0 ?
            <MainContainer>
              <hr id="SignUpLine" />
              <hr id="SimpleLine" />
              <p id="headline">Создайте вашу учётную запись</p>
              <TextField
                id="outlined-basic"
                color="warning"
                size="small"
                label="Адрес электронной почты"
                variant="outlined"
                onChange={getData}
               />
              <TextField id="outlined-basic"
                color="warning"
                size="small"
                label="Пароль"
                variant="outlined"
                type="password"
                onChange={getData}
              />
              <TextField id="outlined-basic"
                color="warning"
                size="small"
                label="Подтвердите пароль"
                variant="outlined"
                type="password" 
              />
              {
                getLogAndPass ?
                localStorage.setItem('Login', login)
                :null
              }
              <Button variant="contained" onClick={() => setGetLogAndPass(true)} >Регистрация</Button>
            </MainContainer> :
            <MainContainer>
              <hr id="SignInLine" />
              <hr id="SimpleLine" />
              <p id="headline">Войти с помощью вашей учётной записи</p>
              <TextField
                id="outlined-basic"
                color="warning"
                size="small"
                label="Адрес электронной почты"
                variant="outlined" />
              <TextField id="outlined-basic"
                color="warning"
                size="small"
                label="Пароль"
                variant="outlined"
                type="password" />
              <Button variant="contained">Войти</Button>
            </MainContainer>
        }
      </MainWindow>
    </>
  );
}

export default SignUp;
