import styled from "styled-components";
import { Link, useLocation, Navigate, Route } from 'react-router-dom';
import '../App.css';
import { TextField, Button } from '@mui/material';
import React, { useState, useEffect } from 'react';
import Api from "../Api";

const MainContainer = styled.div`
  width: 84%;
  margin: 0 auto;
  #SimpleLine{
    margin: unset;
    width: 100%;
    border: none;
    border-top: 1px solid grey;
    opacity: 1;
  }
  #SignInLine{
    margin: unset;
    width: 14%;
    border: none;
    border-top: 2px solid #f56c2d;
    opacity: 1;
  }
  #SignUpLine{
    margin: unset;
    margin-left: 85px;
    width: 50%;
    border: none;
    border-top: 2px solid #f56c2d;
    opacity: 1;
  }
  #headline{
    margin: 30px 0 20px 0px;
    font-size: 24px;
    font-weight: normal;
    color: black;
  }
  .MuiInputBase-root{
    margin-bottom: 20px;
  }
  #outlined-basic{
    width: 350px;
  }
  .MuiButtonBase-root{
    background-color: #f56c2d;
    width: 100%;
    font-size: 14px;
    text-transform: none;
    margin: 30px 0 50px 0px;
    :hover{
      transition: 800ms;
      background-color: #c85e36;
    }
  }
`


const SignUp = () => {

  const [username, setUsername] = useState(null);
  const [password, setPassword] = useState(null);
  const [redirect, setRedirect] = useState(false);

  const Register = () => {
    Api.Auth.signUp(username, password).then((res) => {
      if (res.status === 200 || res.status === 201) {
        setRedirect(true)
      }
    }).catch((error) => console.log('ErrorReg'))
  }

  if (redirect) {
    return <Navigate to='/Dashboard' push />
  }
  
  return(
    <MainContainer>
              <hr id="SignUpLine" />
              <hr id="SimpleLine" />
              <p id="headline">Создайте вашу учётную запись</p>
              <TextField
                id="outlined-basic"
                color="warning"
                size="small"
                label="Адрес электронной почты"
                variant="outlined"
                value={username}
                onChange={(event) => setUsername(event.target.value)}
              />
              <TextField id="outlined-basic"
                color="warning"
                size="small"
                label="Пароль"
                variant="outlined"
                type="password"
                value={password}
                onChange={(event) => setPassword(event.target.value)}
              />
              <TextField id="outlined-basic"
                color="warning"
                size="small"
                label="Подтвердите пароль"
                variant="outlined"
                type="password" />
              <Button onClick={Register} variant="contained">Регистрация</Button>
            </MainContainer>
  );
}


export default SignUp;
