import axios from "axios";

const Auth = {
  signIn: async (login, password) => {
    const res = await axios.post('auth/login', { user: { login, password } })
    if (res.status === 201) {
      const token = res.data.token
      localStorage.setItem('token', token)
    }
    return res;
  }
}

export default { Auth };
