import axios from "axios";

const Auth = {
  signIn: async (username, password) => {
    const res = await axios.post('http://localhost:5000/auth/login', { user: { username, password } })
    if (res.status === 201) {
      const token = res.data.token
      localStorage.setItem('token', token)
    }
    return res;
  },
  signUp: async (username, password) => {
    const res = await axios.post('http://localhost:5000/auth/registration', {user: {username, password} })
    if (res.status === 201) {
      const token = res.data.token
      localStorage.setItem('token', token)
    }
    return res;
  }
}

export default { Auth };
